<script>
export default {
  data() {
    return {
      scrollPosition: null,
      navColor: {
        backgroundColor: 'transparent',
      },
    };
  },
  watch: {
    scrollPosition() {
      if (this.scrollPosition > 20) {
        this.navColor.backgroundColor = '#292c33';
      } else {
        this.navColor.backgroundColor = 'transparent';
      }
    },
  },
  methods: {
    updateScroll() {
      this.scrollPosition = window.scrollY;
    },
  },
  mounted() {
    window.addEventListener('scroll', this.updateScroll);
  },
  destroy() {
    window.removeEventListener('scroll', this.updateScroll);
  },
};
</script>

